const { CART_ADD_ITEM } = require("../constants/cartConstant");

export const cartReducer = (state = { cartItems: [] }, action) => {
  switch (action.type) {
    case CART_ADD_ITEM:
      const item = action.payload;
      const existItem = state.cartItems.find(
        (x) => x.product.id === item.product
      );
      if (existItem) {
        return {
          ...state,
          cartItems: state.cartItems.map((x) =>
            x.product.id === existItem.product ? item : x
          ),
        };
      } else {
        return { ...state, cartItem: [...state.cartItems, item] };
      }
    default:
      return state;
  }
};
